// Creates an Assist Chip
AssistChip(
    onClick = { /* Action to perform when clicked */ },
    label = { Text("Assist Chip") },
    leadingIcon = { Icon(Icons.Filled.Info, contentDescription = "Info") }
)

// Creates a Filter Chip with state management for selection
var selected by remember { mutableStateOf(false) }

FilterChip(
    selected = selected,
    onClick = { selected = !selected }, // Toggle selection
    label = { Text("Filter Chip") },
    leadingIcon = { if (selected) Icon(Icons.Filled.Check, contentDescription = "Selected") } // Show checkmark if selected
)

// Creates an Input Chip (typically removable)
InputChip(
    selected = true, // Example: Initially selected
    onClick = { /* Action to perform when clicked (e.g., remove chip) */ },
    label = { Text("Input Chip") },
    trailingIcon = { Icon(Icons.Filled.Close, contentDescription = "Close") }
)

// Creates a Suggestion Chip
SuggestionChip(
    onClick = { /* Action to perform when clicked */ },
    label = { Text("Suggestion Chip") }
)

// Single selection Chip Group - manages selected chip
var selectedChip by remember { mutableStateOf<String?>(null) } 

ChipGroup(selectedChip = selectedChip) { chip -> 
    FilterChip(
        selected = selectedChip == chip,
        onClick = { selectedChip = chip },
        label = { Text(chip) }
    )
}

// Multi-selection Chip Group
val selectedChips = remember { mutableStateOf(setOf<String>()) }

ChipGroup { 
    listOf("Chip 1", "Chip 2", "Chip 3").forEach { chip ->
        FilterChip(
            selected = chip in selectedChips.value,
            onClick = { 
                selectedChips.value = if (chip in selectedChips.value) {
                    selectedChips.value - chip
                } else {
                    selectedChips.value + chip
                }
            },
            label = { Text(chip) }
        )
    }
}
